import React, {Component} from 'react';
import MovieSearchForm from './MovieSearchForm.js';
import Container from './Container.js';
import API from '../utils/API';
import { Jumbotron } from 'react-bootstrap';
import { Table } from 'react-bootstrap';

class SearchResultsTable extends Component {
    state = {
        result: {},
        search: ""
    };


//Function to query the movie database
searchMovie= query => {
    API.search(query)
        .then(res => this.setState({ result: res.data }))
        .catch(err => console.log (err));
};

handleInputChange = event => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
        [name]: value
    });
};

//On form submission, search movie database 
handleFormSubmit = event => {
    event.preventDefault();
    this.searchMovie(this.state.search);
};

render () {
    return (
        <Container>
            <Jumbotron>
                <h1>Avoxi Skills Test</h1>

              <MovieSearchForm
                value={this.state.search}
                handleInputChange={this.handleInputChange}
                handleFormSubmit={this.handleFormSubmit}
              />

            </Jumbotron>
            <Table striped bordered condensed hover>
            <thead>
    <tr>
      <th>Movie Poster</th>
      <th>Title</th>
      <th>Plot Summary</th>
      <th>Rating</th>
      <th>Director</th>
      <th>Release Date</th>
      <th>Earnings</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td><img src={this.state.result.Poster}></img></td>
      <td>{this.state.result.Title}</td>
      <td>{this.state.result.Plot}</td>
      <td>{this.state.result.Rated}</td>
      <td>{this.state.result.Director}</td>
      <td>{this.state.result.Released}</td>
      <td>{this.state.result.BoxOffice}</td>
    </tr> 
  </tbody>
            </Table>

        </Container>

);
}
}

export default SearchResultsTable;
