import React, {Component} from 'react';
import MovieSearchForm from './MovieSearchForm.js';
import MovieInfo from './MovieInfo.js';
import API from '../utils/API';
import { Container } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import MovieCard  from './MovieCard.js';

class SearchResultsTable extends Component {
    state = {
        results: {},
        search: ""
    };

// When this component mounts, search the Giphy API for pictures of kittens
  componentDidMount() {
    this.searchMovie("jaws");
  }

//Function to query the movie database
searchMovie= query => {
    API.search(query)
        .then(res => this.setState({ results: res.data}))
        .catch(err => console.log (err));
};

handleInputChange = event => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({
        [name]: value
    });
};

//On form submission, search movie database for text entered by user
handleFormSubmit = event => {
    event.preventDefault();
    this.searchMovie(this.state.search);
};

render () {
    return (
        <div>
            <Row>
                <Col size="md-8">
                <MovieCard>
                {this.state.result.title ? (
                    <MovieInfo
                    title={this.state.result.title}
                    src={this.state.result.Poster}
                    director={this.state.result.Director}
                    />
                ) : (
                    <h3>No Results Found</h3>
                )}
                </MovieCard>
                </Col>
            </Row>
        </div>

);
}
}

export default SearchResultsTable;
