import React, {Component} from 'react';
import MovieSearchForm from './MovieSearchForm.js';
import API from '../utils/API';
import { Jumbotron } from 'react-bootstrap';
import { Table } from 'react-bootstrap';
import "./stylesheet.css";

class SearchResultsTable extends Component {
    state = {
        result: {},
        search: ""
    };


//Function to query the movie database
searchMovie= query => {
    API.search(query)
        .then(res => this.setState({ result: res.data }))
        .catch(err => console.log (err));
};

//capture the text entered by the user
handleInputChange = event => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
        [name]: value
    });
};

//On form submission, search movie database 
handleFormSubmit = event => {
    event.preventDefault();
    this.searchMovie(this.state.search);
};

//Display search box in jumbotron and results in table form
render () {
    return (
    <div>
        <Jumbotron className="bg">
            <h2>Avoxi Skills Test</h2>
            <h4>Get the most up to date info about your fave movies.</h4>

                <MovieSearchForm
                value={this.state.search}
                handleInputChange={this.handleInputChange}
                handleFormSubmit={this.handleFormSubmit}
                />

        </Jumbotron>
        <Table striped bordered className="movieTable">
            <thead>
                <tr>
                    <th>Movie Poster</th>
                    <th>Title</th>
                    <th>Plot Summary</th>
                    <th>Rating</th>
                    <th>Director</th>
                    <th>Release Date</th>
                </tr>
        </thead>
        <tbody>
                <tr>
                    <td><img src={this.state.result.Poster} alt={this.state.result.Title}></img></td>
                    <td>{this.state.result.Title}</td>
                    <td>{this.state.result.Plot}</td>
                    <td>{this.state.result.Rated}</td>
                    <td>{this.state.result.Director}</td>
                    <td>{this.state.result.Released}</td>
                </tr> 
        </tbody>
        </Table>
    </div>  
);
}
}

export default SearchResultsTable;
