import React, {Component} from 'react';
import Title from './Title.js';
import MovieSearchForm from './MovieSearchForm.js';
import MovieInfo from './MovieInfo.js';
import API from '../utils/API';
import Col  from './Col.js';
import Row  from './Row.js';
import MovieCard  from './MovieCard.js';
import { Jumbotron } from 'react-bootstrap';
import { Table } from 'react-bootstrap';

class SearchResultsTable extends Component {
    state = {
        result: {},
        search: ""
    };


//Function to query the movie database
searchMovie= query => {
    API.search(query)
        .then(res => this.setState({ result: res.data }))
        .catch(err => console.log (err));
};

handleInputChange = event => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
        [name]: value
    });
};

//On form submission, search movie database 
handleFormSubmit = event => {
    event.preventDefault();
    this.searchMovie(this.state.search);
};

render () {
    return (
        <div>
            <Jumbotron>
                <Title/>
            <MovieCard>
              <MovieSearchForm
                value={this.state.search}
                handleInputChange={this.handleInputChange}
                handleFormSubmit={this.handleFormSubmit}
              />
            </MovieCard>
            </Jumbotron>
            <Table>
            <thead>
    <tr>
      <th>Title</th>
      <th>Director</th>
      <th>Release Date</th>
      <th>Earnings</th>
      <th>Movie Poster</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>{this.state.result.Title}</td>
      <td>{this.state.result.Director}</td>
      <td>{this.state.result.Released}</td>
      <td>{this.state.result.BoxOffice}</td>
      <td><MovieCard/></td>
    </tr>

  </tbody>
            </Table>

            <Row>
                <Col size="md-6">
                <MovieCard>
                 {this.state.result.Title ? (
                    <MovieInfo
                    title={this.state.result.title}
                    src={this.state.result.Poster}
                    />
                 ) : ( 
                     <h3></h3>
                 )}
                </MovieCard>
                </Col>
                <Col size="md-6">

          </Col>

            </Row>
        </div>

);
}
}

export default SearchResultsTable;
